const initialState = {
        posts: [],
        filter_category: '',
}

function postReducer(state=initialState, action) {
        switch (action.type){
                case "FETCH_POSTS_SUCCESS":
                        console.log('fetch post success')
                        return {
                                posts: action.payload.posts
                        };
                case "FETCH_POSTS_FAILURE":
                        console.log('fetch post fail')
                        return state;

                case "FILTER_POSTS":
                        const curr_posts = state;
                        const filter_posts = curr_posts.filter(post => post.category===action.payload.category)
                        return filter_posts
                        // {
                        //         posts: state.filter(post => post.category===action.payload.category)
                        // };
                default:
                        return state;
        }

}

export default postReducer;
