const initialState = {
  articles: []
};


function rootReducer(state = initialState, action) {
        switch (action.type) {
                case "ADD_USER":
                return Object.assign({}, state, {
                                articles: state.articles.concat(action.payload)
                        });
                default:
                        return state;
        }
};


export default rootReducer;
