const initialState = {
        post: {},
}

function detailReducer(state = initialState, action) {
        switch (action.type) {
                case "FETCH_POST_SUCCESS":
                        return {user: action.payload}
                case "FETCH_POST_ERROR":

                        return state;

                default:
                        return state;
        }
};

export default detailReducer;
