const initialState = {
        user: null,
        fetching: false,
        fetched: false,
        error: null,
};

function userReducer(state = initialState, action) {
        switch (action.type) {
                case "GET_USER":
                        return state;
                case "ADD_USER":
                        console.log("add user reducer")
                        return Object.assign({}, state, {
                                        user: state.user.concat(action.payload)
                                });
                default:
                        return state;
        }
};

export default userReducer;
