import React, { Component } from 'react';
import {Card, Badge} from 'react-bootstrap';
import { connect } from "react-redux";


import {fetchPost} from '../action/detailAction'
import './PostDetail.scss'

class PostDetail extends Component {

        componentDidMount() {
                const post_id = this.props.match.params.post_id;
                this.props.dispatch(fetchPost(post_id));
        }

        render() {
                const {post, user} = this.props;
                return (
                        <Card>
                          <Card.Header>{post.title}</Card.Header>
                          <Card.Body>
                            <blockquote className="blockquote mb-0">
                              <p>
                                {' '}
                                {post.context}{' '}
                              </p>
                              <footer className="blockquote-footer">
                                {user.username}
                              </footer>
                            </blockquote>
                            <div className='other_info'>
                                <Badge className='tag_name' variant="secondary">{post.category}</Badge>
                                <div className='like_post'>
                                    <FaThumbsUp/>
                                    <p className='like_number'>{post.likeCount}</p>
                                </div>
                            </div>
                          </Card.Body>
                        </Card>
                )
        }
}

function mapStateToProps(state) {
        console.log(state);
        return {
                post: state.detailReducer.post,
                user: state.userReducer.user
        }
}

export default connect(mapStateToProps)(PostDetail);
